<template>
  <div id="app" class="container-fluid">
    <h1>
      <small><router-link :to="{ name: 'inventories'Â }"><i class="fa fa-arrow-left"></i></router-link></small>
      Tag the computer on
      <small class="text-muted">{{ $route.params.inventory }}</small> 
      with {{ getUSBName($route.params.inventory) }}
      <small class="text-muted">{{ $route.params.serial }}</small>
    </h1>
    <div v-html="html"></div>
    <button class="btn btn-primary" type="button" @click="tag_computer">Tag it</button>
  </div>
</template>

<script>
import axios from 'axios'

import Instascan from 'instascan'

import 'css-toggle-switch/dist/toggle-switch.css'

const server = process.env.SERVER_URL || 'http://localhost:8090'

function beep () {
  var snd = new Audio('data:audio/wav;base64,')
  snd.play()
}

export default {
  name: 'config',
  data () {
    return { html: '', scanner: null, camera: null }
  },
  methods: {
    getUSBName (inventory) {
      var usb = this.$store.getters.plugged_usbs[inventory]
      return usb['vendor'] + ' ' + usb['product']
    },
    toggleScan () {
      var preview = document.getElementsByClassName('qr-preview')[0]
      if (this.camera === null) {
        preview.classList.remove('d-none')
        this.initScan()
      } else {
        preview.classList.add('d-none')
        this.scanner.stop()
        this.camera = null
      }
    },
    initScan () {
      if (this.scanner === null) {
        this.scanner = new Instascan.Scanner({ video: document.getElementsByClassName('qr-preview')[0] })
        this.scanner.addListener('scan', function (content) {
          var parts = content.split('/')
          document.getElementsByClassName('qr-input')[0].value = parts[parts.length - 1]
          beep()
        })
      }

      Instascan.Camera.getCameras().then(function (cameras) {
        if (cameras.length > 1) {
          this.camera = 1
          this.scanner.start(cameras[1])
        } else if (cameras.length > 0) {
          this.camera = 0
          this.scanner.start(cameras[0])
        } else {
          console.log('No cameras found')
        }
      }.bind(this))
    },
    tag_computer () {
      var form = document.getElementsByTagName('form')[0]
      var values = new FormData()

      for (var i = 0; i < form.elements.length; i++) {
        if (form.elements[i].type === 'radio') {
          if (form.elements[i].checked === true) {
            values.append(form.elements[i].name, form.elements[i].value)
          }
        } else {
          values.append(form.elements[i].name, form.elements[i].value)
        }
      }

      axios.post(server + '/tag_computer_form?inventory=' + this.$route.params.inventory, values)
        .then(function (response) {
          this.$store.dispatch('Flash', {'msg': response.data.msg, 'severity': 'success'})
          this.$router.push({name: 'inventories'})
        }.bind(this)).catch(function (error) {
          this.html = error.response.data
        }.bind(this))
    }
  },
  beforeMount () {
    axios.get(server + '/tag_computer_form').then(function (response) {
      this.html = response.data
    }.bind(this)).then(function () {
      document.getElementsByClassName('fa-qrcode')[0].addEventListener('click', this.toggleScan.bind(this))
    }.bind(this))
  }
}
</script>